import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = ({ signupData }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform login authentication logic here
    // You can use APIs or other authentication mechanisms
    const matchedUser = signupData.find((user) => user.username === username && user.password === password);
    if (matchedUser) {
      console.log('Login successful');
      // Perform further actions for successful login, such as redirecting to a dashboard or setting authentication state
    } else {
      console.log('Invalid username or password');
      // Handle error for invalid credentials, such as showing an error message
    }

    // Reset form fields
    setUsername('');
    setPassword('');
  };

  const isFormValid = username && password;

  return (
    <div>
      <center>
        <h2>Login</h2>
      </center>
      <form onSubmit={handleSubmit}>
        <div>
          <center>
            <label>Username:</label>
            <input type="text" value={username} onChange={handleUsernameChange} required />
          </center>
        </div>
        <div>
          <center>
            <label>Password:</label>
            <input type="password" value={password} onChange={handlePasswordChange} required />
          </center>
        </div>
        <div className="ms-5">
          <center>
            {isFormValid ? (
              <Link to="/home">
                <button type="submit" className="btn btn-primary mx-5">
                  Login
                </button>
              </Link>
            ) : (
              <button type="submit" className="btn btn-primary mx-5" disabled>
                Login
              </button>
            )}
            <Link to="/signup">
              <button type="button" className="btn btn-success">
                Signup
              </button>
            </Link>
          </center>
        </div>
      </form>
    </div>
  );
};

export default Login;
